jQuery.fn.multicheck=function(){$(this).each(function(e,t){var n=$(t).attr("name");var r=$(t).val();var i=$("<div/>",{"class":"multicheck"});jQuery.each($(t).find("option"),function(){var e=$(this).attr("value");var t=$(this).html();var s=$("<div/>");var o=n+"_"+e;s.append($("<input/>",{type:"checkbox",name:n,value:e,id:o,checked:-1!=jQuery.inArray(e,r)}));s.append($("<label/>",{"for":o,html:t}));i.append(s)});$(t).after(i);$(t).remove()})}
